import logo from './logo.svg';
import axios from 'axios'
import './App.css';
import {useState, useEffect} from 'react'



const App = () =>{


const [cars, setCars] = useState([])
const [newImage, setNewImage] = useState('')
const [newMake, setNewMake] = useState('')
const [newModel, setNewModel] = useState('')
const [newYear, setNewYear] = useState('')
const [newPrice, setNewPrice] = useState('')
const [newMileage, setNewMileage] = useState('')

  useEffect(()=>{
    axios
        .get("http://localhost:3000/cars")
        .then((response)=>{
          setCars(response.data)
        })
  },[])




// handle new change functions
const handleImageChange=(event)=>{
  setNewImage(event.target.value)
}

const handleMakeChange = (event)=>{
  setNewMake(event.target.value)
}
const handleModelChange =(event)=>{
  setNewModel(event.target.value)
}

const handleYearChange =(event)=>{
  setNewYear(event.target.value)
}

const handlePriceChange =(event)=>{
  setNewPrice(event.target.value)
}

const handleMileageChange =(event)=>{
  setNewMileage(event.target.value)
}

const resetState=()=>{
  setNewImage("")
  setNewMake("")
  setNewModel('')
  setNewYear("")
  setNewPrice("")
  setNewMileage("")
}

const resetYear=()=>{
  setNewYear('')
}

// to be able to add add Cars
const handleNewCarFormSubmit = (event)=>{
  event.preventDefault()
  axios.post(
    "http://localhost:3000/cars",
    {
      image: newImage,
      make: newMake,
      model: newModel,
      year: newYear,
      price: newPrice,
      mileage: newMileage

    }
  ).then(()=>{
    axios
        .get("http://localhost:3000/cars")
        .then((response)=>{
          setCars(response.data)
        })
  })
  resetState()
}

// to be able to delete a car
const handleDeleteCar =(carData)=>{
  axios
      .delete(`http://localhost:3000/cars/${carData._id}`)
      .then(()=>{
        axios
            .get('http://localhost:3000/cars')
            .then((response)=>{
              setCars(response.data)
            })
      })
}

const handleEdit=(event, carData)=>{
  event.preventDefault()
  axios
      .put(
        `http://localhost:3000/cars/${carData._id}`,
        {
          image: newImage,
          make: newMake,
          model: newModel,
          year: newYear,
          price: newPrice,
          mileage: newMileage
        }
      )
      .then(()=>{
        axios
            .get('http://localhost:3000/cars')
            .then((response)=>{
              setCars(response.data)
            })
      })
      resetState()
}




return(
  <>
  <h1> Car Dealership Website</h1>

      <form onSubmit={handleNewCarFormSubmit}>
            <h2>Add a New Car</h2>
            Image: <input type="text" value={newImage} onChange={handleImageChange}></input> <br/>
            Make: <input type="text" value={newMake} onChange={handleMakeChange}></input> <br/>
            Model: <input type="text" value={newModel} onChange={handleModelChange}></input> <br/>
            Year: <input type="text" value={newYear} onChange={ handleYearChange}></input><br/>
            Price: <input type="text" value={newPrice} onChange={ handlePriceChange}></input><br/>
            Mileage: <input type="text" value={newMileage} onChange={ handleMileageChange} ></input><br/> <br/>
            <input type="Submit" value="Submit"></input><br/>


      </form> <br/> <br/> <br/>
      <div>
            {
              cars.map((car)=>{
                return(
                  <div>

                      <img class="btn" src={car.image}/> <br/> <br/>

                      <details>
                            <summary>Edit</summary>
                            <form onSubmit={(event)=>{handleEdit(event, car)}} key={car._id}>
                                Image: <input type="text" onChange={handleImageChange} placeholder={car.image}></input> <br/>
                                Make: <input type="text" onChange={handleMakeChange} placeholder={car.make}></input> <br/>
                                Model: <input type="text" onChange={handleModelChange} placeholder={car.model}></input> <br/>
                                Year: <input type="text" onChange={handleYearChange} placeholder={car.year}></input><br/>
                                Price: <input type="text" onChange={handlePriceChange} placeholder={car.price}></input><br/>
                                Mileage: <input type="text" onChange={handleMileageChange} placeholder={car.mileage}></input><br/>
                                <input type="Submit" value="Submit"></input><br/>
                            </form>
                      </details>

                      <h1>{car.make}</h1>
                      <h2>{car.model}</h2>
                      <h3>year: {car.year}</h3>
                      <h4>Price: ${car.price}</h4>
                      <h5>Mileage: {car.mileage}</h5>
                      <input type="submit" onClick={(event)=>{handleDeleteCar(car)}} value="Delete" /> <br/> <br/><br/><br/>


                  </div>

                )

              })
            }
      </div>

  </>
)
}

export default App;
